import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3';
import lottery from './lottery';

class App extends Component{

  constructor(props){
    super(props);

    this.state ={manager:'', Owner:'',contractor_balance:'',owner_balance:'',contractor:'', value:'',value2:'',apartment_numbers:[],Get_units:{},get_units_visible:false, Get_flats:{},get_flats_visible:false}
  }

  async componentDidMount(){
    const Owner = web3.currentProvider.selectedAddress;
    const manager =  await lottery.methods.countUnits().call();
    const contractor =  await lottery.options.address;
    const owner_balance = await web3.eth.getBalance(web3.currentProvider.selectedAddress);
    const apartment_numbers = await lottery.methods.getAppartmentUnits().call();
    const contractor_balance = await web3.eth.getBalance(lottery.options.address);
    this.setState({
      manager,
      Owner,
      contractor_balance,
      owner_balance,
      contractor,
      apartment_numbers
    })
  }

  onSubmit = async (event) => {
    event.preventDefault();
    const accounts = await web3.eth.getAccounts()
    const units_identifier = await lottery.methods.getUnit(this.state.value).call();

    // .send({
    //   from:accounts[0],
    //   value:this.state.value
    // });
    console.log(accounts)
    console.log(units_identifier)
    this.setState({
      get_flats_visible:true,
      Get_flats :units_identifier
    })
  }

  onUnitSubmit= async (event) => {
    event.preventDefault();
    // const accounts = await web3.eth.getAccounts()
    const units_identifier2 = lottery.methods.unitNumbers(this.state.value2).send({
        from:accounts[0],
        value:this.state.value
      });;

    console.log(units_identifier2)
    // this.setState({
    //   get_units_visible:true,
    //   Get_units :units_identifier2
    // })
  }
  render(){

    return (
      <div>
        <h2>Owner: {this.state.Owner}</h2>
        <h2>Contractor: {this.state.contractor}</h2>
        <h2>Owner Balance: {this.state.owner_balance}</h2>
        <h2>Contractor Balance: {web3.utils.fromWei(this.state.contractor_balance, 'ether')}</h2>
        <h2>Count Units: {web3.utils.fromWei(this.state.manager, 'ether')}</h2>
        <h2>Apartment Assigned: {this.state.apartment_numbers}</h2>
        <hr />
        <form onSubmit={this.onSubmit}>
          <h2>Get Flat Details</h2>
          <input
            value={this.state.value}
            onChange={event => this.setState({ value: event.target.value})}
          />
          {this.state.get_flats_visible === true ? 
          <div> 
            <p>Owner:  {this.state.Get_flats[1]}</p>
            <p>Tenant:  {this.state.Get_flats[2]}</p>
            <p>Flat_number:  {this.state.Get_flats[4]}</p>
          </div>:null}
          <button>Enter</button>
        </form>
        <form onSubmit={this.onUnitSubmit}>
          <h2>Get Particular Unit Id</h2>
          <input
            value={this.state.value2}
            onChange={event => this.setState({ value2: event.target.value})}
          />
          {this.state.get_units_visible === true ? 
          <div> 
            <p>Owner:  {this.state.Get_units}</p>
            {/* <p>Tenant:  {this.state.Get_units[2]}</p>
            <p>Flat_number:  {this.state.Get_units[4]}</p> */}
          </div>:null}
          <button>Enter</button>
        </form>
      </div>
    );
  }
}
