import {
  SET_CURRENT_PROFILE,
  PROFILE_LOADING
} from "../actions/types";

const initialState = {
  profile: {},
  loading: false
};

export default function (state = initialState, action) {
  switch (action.type) {
    case PROFILE_LOADING:
      return {
        ...state,
        loading: true
      };
    case SET_CURRENT_PROFILE:
      return {
        ...state,
        profile: action.payload,
        loading: true
      };
    default:
      return state;
  }
}
