import React from 'react';
import { Grid, GridColumn as Column, GridToolbar } from '@progress/kendo-react-grid';
import { ExcelExport } from '@progress/kendo-react-excel-export';
import products from './apartments.json';
import { MyCommandCell } from './actioneditbutton.js';
import { filterBy } from '@progress/kendo-data-query';
import { Input } from '@progress/kendo-react-inputs';
import MyInventoryAnchorTag from './GridAnchorTag.js';
import { formatDate } from '@telerik/kendo-intl';
// import '../../css/inventerGrid.css'
import '../../../css/inventerGrid.css'
import { Link } from "react-router-dom";

products.forEach(o => {    o.orderDate = formatDate(new Date(o.orderDate), { date: "long" });
    o.expiryDate = formatDate(new Date(o.expiryDate), { date: "long" });
    o.shippedDate = o.shippedDate === 'NULL' ? undefined : new Date(o.shippedDate);
});
class App extends React.Component {
    _export;
    export = () => {
        this._export.save();
    }
    render() {
        return (
            <ExcelExport
                data={products}
                ref={exporter => this._export = exporter}
            >
                <Grid data={products} style={{ height: '420px' }}>
                    <GridToolbar>
                        <button
                            title="Export PDF"
                            className="k-button k-primary"
                            onClick={this.export}
                        >
                            Export to Excel
                    </button>
                    </GridToolbar>
                    <GridColumn field="ProductID" title="Product ID" width="50px" />
                    <GridColumn field="ProductName" title="Product Name" width="350px" />
                    <GridColumn field="UnitPrice" title="Price" />
                    <GridColumn field="UnitsInStock" title="In stock" />
                    <GridColumn field="Discontinued" title="Discontinued" />
                </Grid>
            </ExcelExport>
        );
    }
}
export default App;
