import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3';
import lottery from './lottery';

class App extends Component{

  constructor(props){
    super(props);

    this.state ={manager:'', Owner:'',contractor_balance:''}
  }

  async componentDidMount(){
    const Owner = web3.currentProvider.selectedAddress;
    const manager =  await lottery.methods.countUnits().call();
    const owner_balance = await web3.eth.getBalance(Owner)
    const contractor_balance = await web3.eth.getBalance(lottery.options.address)
    this.setState({
      manager,
      Owner,
      contractor_balance,
      owner_balance
    })
  }

  render(){

    return (
      <div>
        <h2>Owner: {this.state.Owner}</h2>
        <h2>Owner Balance: {this.state.contractor_balance}</h2>
        <h2>Contractor Balance: {this.state.contractor_balance}</h2>
        <h2>Count Units: {this.state.manager}</h2>

      </div>
    );
  }
}
