import React from 'react';
import { Grid, GridColumn as Column, GridToolbar } from '@progress/kendo-react-grid';
import { ExcelExport } from '@progress/kendo-react-excel-export';
import products from './products.json';
import { MyCommandCell } from './actioneditbutton.js';
import { filterBy } from '@progress/kendo-data-query';
import { Input } from '@progress/kendo-react-inputs';
import MyInventoryAnchorTag from './GridAnchorTag.js';
import { formatDate } from '@telerik/kendo-intl';
// import '../../css/inventerGrid.css'
import '../../../css/inventerGrid.css'
import { Link } from "react-router-dom";

products.forEach(o => {
    o.orderDate = formatDate(new Date(o.orderDate), { date: "long" });
    o.expiryDate = formatDate(new Date(o.expiryDate), { date: "long" });
    o.shippedDate = o.shippedDate === 'NULL' ? undefined : new Date(o.shippedDate);
});
class Apartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = this.createState(0, 10);
        this.pageChange = this.pageChange.bind(this);

    }

    lastSelectedIndex = 0;
    CommandCell;
    AnchorTag;
    _export;
    export = () => {
        this._export.save();
        this.setState({
            search: false
        })
    }
    AnchorTag = MyInventoryAnchorTag("inEdit");
    state = {
        data: products.map(dataItem => Object.assign({ selected: false }, dataItem)),
        skip: 0, take: 10,
        sort: [
            { field: '', dir: 'asc' }
        ],
        search: false,
        deleteButton: false,
        count: 0,
        flagdisabled: ""
    }
    CommandCell = MyCommandCell({
        editField: this.editField
    });


    pageChange(event) {
        this.setState(this.createState(event.page.skip, event.page.take));
    }

    createState(skip, take) {
        return {
            items: products.slice(skip, skip + take),
            total: products.length,
            skip: skip,
            pageSize: take,
            pageable: {
                buttonCount: 0,
                info: true,
                type: 'numeric',
                pageSizes: true,
                previousNext: true
            }
        };
    }

    selectionChange = (event) => {
        event.dataItem.selected = !event.dataItem.selected;
        this.forceUpdate();
        const countingData = []
        for (let i = 0; i <= this.state.items.length; i++) {
            if (this.state.items[i] !== undefined) {
                if (this.state.items[i]['selected'] === true) {
                    countingData.push(this.state.items[i])
                }
            }
        }
        var counting = countingData.length;
        if (counting !== 0) {
            this.setState({
                deleteButton: true,
                count: countingData.length
            })
        } else {
            this.setState({
                deleteButton: false,
                count: 0
            })
        }
    }

    rowClick = (event) => {
        let last = this.lastSelectedIndex;
        const current = this.state.data.findIndex(dataItem => dataItem === event.dataItem);

        if (!event.nativeEvent.shiftKey) {
            this.lastSelectedIndex = last = current;
        }

        if (!event.nativeEvent.ctrlKey) {
            this.state.data.forEach(item => item.selected = false);
        }
        const select = !event.dataItem.selected;
        for (let i = Math.min(last, current); i <= Math.max(last, current); i++) {
            const productData = this.state.data[i]
            productData.selected = select;
        }
        this.forceUpdate();
    }
    enterEdit = (dataItem) => {
        this.setState({
            data: this.state.data.map(item =>
                item._id === dataItem._id ?
                    { ...item, inEdit: true } : item
            )
        });
    }
    headerSelectionChange = (event) => {
        const checked = event.syntheticEvent.target.checked;

        if (checked === true) {
            this.setState({
                deleteButton: true,
                count: this.state.items.length
            })
        } else {
            this.setState({
                deleteButton: false,
                count: 0
            })
        }
        this.state.items.forEach(item => item.selected = checked);
        this.forceUpdate();
    }
    handleChange = (event) => {
        this.setState({
            items: filterBy(products.map(dataItem => Object.assign({ selected: false }, dataItem)), {
                logic: "or",
                filters: [{ field: "ProductName", operator: "contains", value: event.target.value },
                { field: "UnitPrice", operator: "contains", value: event.target.value },
                { field: "UnitsInStock", operator: "contains", value: event.target.value },
                { field: "ReorderLevel", operator: "contains", value: event.target.value },
                ]
            })
        });
    };
    onClickButton = (event) => {
        if (event === "cancel") {
            this.setState({
                search: false
            })
        }
        if (event === "search") {
            this.setState({
                search: true
            })

        }

    }
    onClickEditButton = () => {

        this.setState({
            flagdisabled: true
        })


    }
    render() {


        return (
            <div>
                <br />
                <div style={{ textAlign: "left", fontSize: "12px", marginLeft: "50px", color: "black" }}>
                <Link>Inventory</Link> > <Link>Product</Link> 
                </div>
                <br />
                <div className="" style={{ marginLeft: "4%" }}>
                    <div className="" style={{ width: "95%", marginLeft: "2%" }}>


                        <ExcelExport
                            data={this.state.data}
                            ref={exporter => this._export = exporter}
                        >
                            {this.state.deleteButton === true ?
                                <div className="deleteDiv">
                                    <div className="">
                                        <label>{this.state.count} row(s) selected</label>
                                        <button
                                            className="k-button deleteButton"

                                        ><span className="k-icon k-i-delete " style={{ float: "right", fontSize: '25px' }}></span>
                                        </button>
                                    </div>
                                    {this.state.count > 1 ? <div className="editDiv">
                                        <button
                                            className="k-button deleteButton"
                                            style={{ marginTop: "-45px" }}
                                            disabled

                                        ><span className="k-icon k-i-pencil"
                                            style={{ float: "right", fontSize: '25px' }}></span>
                                        </button>
                                    </div> :
                                        <button
                                            className="k-button deleteButton"
                                            style={{ marginTop: "-45px" }}
                                        ><span className="k-icon k-i-pencil" style={{ float: "right", fontSize: '25px' }}></span>
                                        </button>}

                                </div>
                                :
                                null}
                            {this.state.search === true ?
                                <Input
                                    className="k-textbox"
                                    onChange={this.handleChange}
                                    style={{ float: "left", marginLeft: "20px", marginTop: "10px" }}
                                /> : <div
                                    style={{ fontFamily: "Roboto ,Helvetica, Arial, sans-serif ", float: "left", marginTop: "10px", fontSize: "20px", fontWeight: "500", color: "rgba (0,0,0,0.87)" }}
                                ><span className="Grid-header">Product</span></div>}
                            {this.state.search === true ? <button
                                className="k-button"
                                name="hello"
                                onClick={() => { this.onClickButton("cancel") }}
                                style={{ float: "left", boxShadow: "none", color: "#586069", marginTop: "10px" }}
                            >X
                </button> : null}

                            <GridToolbar className="Grid_excel_button">
                                <button
                                    title="Add"
                                    type="button"
                                    to="#"
                                    className="k-button role-main-Link-plus-button"
                                    style={{ float: "right" }}
                                >
                                    <span
                                        className="k-icon k-i-plus"
                                        style={{ marginLeft: "0px" }}>
                                    </span>
                                </button>


                                <button
                                    title="Print"
                                    className="k-button"
                                    onClick={this.export}
                                    style={{ float: "right", boxShadow: "none", color: "#586069" }}
                                >
                                    <span className="k-icon k-i-print "></span>
                                </button>

                                <button
                                    title="Export PDF"
                                    className="k-button"
                                    onClick={this.export}
                                    style={{ float: "right", boxShadow: "none", color: "#586069" }}
                                >
                                    <span className="k-icon k-i-download"></span>
                                </button>

                                <button
                                    title="Search"
                                    className="k-button"
                                    onClick={() => { this.onClickButton("search") }}
                                    style={{ float: "right", boxShadow: "none", color: "#586069" }}
                                >
                                    <span className="k-icon k-i-zoom k-i-search"></span>
                                </button>

                            </GridToolbar>

                            <Grid
                                className=""
                                style={{ fontFamily: "Roboto ,Helvetica, Arial, sans-serif ", fontSize: "14px", fontWeight: "400", width: "105%", marginLeft: "-22px" }}
                                data={this.state.items}
                                // data={orderBy(this.state.data.slice(this.state.skip, this.state.take + this.state.skip), this.state.sort)}
                                skip={this.state.skip}
                                selectedField="selected"
                                onSelectionChange={this.selectionChange}
                                onHeaderSelectionChange={this.headerSelectionChange}
                                take={this.state.take}
                                total={this.state.total}
                                // total={this.state.data.length}
                                // pageable={true}
                                pageable={this.state.pageable}
                                pageSize={this.state.pageSize}
                                onPageChange={this.pageChange}
                                sortable={true}
                                sort={this.state.sort}
                                onSortChange={(e) => {
                                    this.setState({
                                        sort: e.sort
                                    });
                                }}
                            >
                                <Column
                                    className="check-box-color"
                                    field="selected"
                                    width="70px"
                                    headerSelectionValue={
