                                    }
                                />
                                {/* <Column width="70px" filterable={false} cell={this.CommandCell} title="Edit"/>  */}
                                <Column field="product" title="Name" />
                                <Column field="productType" title="Type" />
                                <Column field="MaintainInventory" title="Maintain Inventory" />
                                {/* <Column field="Inventory" title="Inventory" /> */}
                                <Column field="Active" filter="numeric" title="Active" />
